function mpf_showElementSize(e,i="img"){let t,n,m;"img"==i?(t=Math.round(e.querySelector("img").height),n=Math.round(e.querySelector("img").width)):(t=Math.round(e.height),n=Math.round(e.width)),"element-size"==e.firstChild.className?e.firstChild.innerText=t+" x "+n:((m=document.createElement("div")).className="element-size",m.innerText=t+" x "+n+" - ",e.insertBefore(m,e.firstChild))}function mpf_checkElementSize(){let e=document.querySelectorAll(".show-size-img");e.forEach(function(e){mpf_showElementSize(e,"img")}),(e=document.querySelectorAll(".show-size-div")).forEach(function(e){mpf_showElementSize(e,"div")})}window.addEventListener("load",mpf_checkElementSize),window.addEventListener("resize",mpf_checkElementSize);