$(window).on("load",function(){$(".size-img").each(function(){var i=Math.round($(this).find("img").height()),n=Math.round($(this).find("img").width());$(this).prepend('<div class="element-size">'+i+" x "+n+"</div>")})}),$(window).on("resize",function(){$(".size-img").each(function(){var i=Math.round($(this).find("img").height()),n=Math.round($(this).find("img").width());$(this).find(".element-size").text(i+" x "+n)})});